		freetype = $.freetype
		if(freetype==3)return;

		push('༺ˇ»`ʚ'+$.freetype_name+'ɞ´«ˇ༻', null, 1, freetype);
		$.categories.map($=>{
				category_id = $.category_id
				push('༺ '+$.category_name+' ༻', $$(freetype,category_id,sk,fk), 1, freetype);
				["综合", "热门", "最新"].map((title,sk)=>{
