        }
    });
    host = `http://api.${domain}.com`;
}
getData = (uri) => {
    url = host + uri + headers;
    // java.log(url);
    data = java.ajaxAll([url])[0];
}
vuri = "/search?keyword=深空彼岸";
time = +java.timeFormatUTC(new Date(), 'yyyyMMdd', 8);
arr1 = []; // 成功
arr2 = []; // 失败
arr3 = []; // 新增
if ($.time < time) {
    java.toast("检测更新中");
    try {
        for (i in $.domains) {
            arr1.push($.domains[i]);
            arr2.push($.domains[i]);
            getHost(i);
            try {
                getData(vuri); //校验搜索
                if (data.code() == 200) {
                    arr2.length = arr2.length - 1;
                    try {
                        getData(_path.GetHost);
                        domain = JSON.parse(data.body())
                            .data.landingPageDomain;
                        arr3.push([
                            domain.match(_reg)[1],
                            uType
                        ]);
                    } catch (err) {}
                } else {
                    arr1.length = arr1.length - 1;
                }
            } catch (err) {
                arr1.length = arr1.length - 1;
            }
        }
        array = arr1.concat(arr3);
        $.domains = array.filter(function(item, index) {
            return array.findIndex(function(value) {
                return value[0] === item[0] && value[1] === item[1];
            }) === index && arr2.findIndex(function(value) {
                return value[0] === item[0] && value[1] === item[1];
            })
        });
        $.time = time
        setv($);
        java.toast("接口更新成功");
    } catch (err) {
        java.toast("未知错误：" + err);
    }
}
run = Path => {
    for (i in $.domains) {
        getHost(i);
        try {
            getData(java.hexDecodeToString(result));
            if (data.code() == 200)
                return $$ = JSON.parse(data.body())[Path];
        } catch (err) {}
    }
    return $$ = {};
}
