var p=f.select("input")
var a=f.attr("action")
var list=[];
if(web&&html){
	length=p.length>3?2:p.length;
	for(i=0;i<length;i++){
	name=p[i].attr("name")
	value=p[i].attr("value")
	clas=p[i].attr("class")
	if(value!=""){
	add=`${name}=${value}`
	}else if(clas=="text"){
		add=`${name}=${key}`}
	//java.log(add)
	list.push(add)}
