// 正文链接动态添加Referer头
// 自动更新正文图片密钥
let domain = ".";
/**
 *@params options {Options|null}
 */
Object.prototype.addOptions=function(options){let j={"Referer":baseUrl.replace(/,[\s\S]+\}/,"")};if(!this.toString().includes(domain)){j.Referer=""};options=options||{};options.headers=Object.assign(options.headers||{},j);return options?`${this.toString()},${JSON.stringify(options)}`:this.toString()};
/**
 *@params func {Function|null}
 *@params options {Options|null}
 */
Object.prototype.toImg=function(func,options){
	let j=(u)=>`<img src="${u.addOptions(options)}">`,h=(u,i,t)=>typeof func=="function"?j(func(u,i,t)):j(u);return Array.isArray(this)?this.map(h).join("\n"):h(this.toString())};
function load(t){for(let o of t){let j = java.ajax(o);if(!/exception/.test(j))return[j,o]}throw"Error: 所有链接下载失败"}
//阅读后台WebView解析密钥
function parseKey(jsUrl){
jsBaseUrls=[
"https://raw.githubusercontents.com/Xwite/decodeObfuscator/main/", 
"https://raw.githubusercontent.com/Xwite/decodeObfuscator/main/", 
  "https://cdn.jsdelivr.net/gh/Xwite/decodeObfuscator@main/"];

urls=["plugins/common.js",
"plugins/manwa.js",
"plugins/evalPacker.js",
"tools/babel_asttool.js",
"tools/config.js",
"tools/decode.js",
"tools/decodeOb.js",
"tools/usefulPlugins.js",
];
jsBaseUrl = cache.get("decodeObfuscator")
if (!jsBaseUrl) {
jsBaseUrl = load(jsBaseUrls)[1];
java.toast(jsBaseUrl);
cache.put("decodeObfuscator", jsBaseUrl)
}
String.prototype.toAbsUrl=function(){
	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
};
java.log(jsBaseUrl);
String.prototype.toAbsUrl=function(){
	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
}
//解决跨域js加载问题
function loadJS(urls){

	return jsBaseUrl && urls.map(url=>script(java.importScript(url.toAbsUrl())))
	.join("\n") || ""
	}

function script(content){
	return `<script>${content};</script>`
	}
	java.log(">>>>> 本地密钥解密开始");
//html片段
html = `<html><head>${loadJS(urls)}</head><body></body><script>
function HttpGetText(url){return fetch(url).then(res=>res.text()).catch(err=>alert(err));};
HttpGetText("${jsUrl}").then(js=>decode(js,[manwa])).catch(e=>keys=e);
</script></html>`;
key = JSON.parse(java.webView(html, source.getVariable(),"keys"))[0];
java.log(key);
return key
}

