@js:
var read=parseInt(result[0]);
if(read>10000)
 result[0]=(read/ 10000).toFixed(1)+'万';
result[0]=result[0]+'人在读';
var state='';
switch(parseInt(result[1])){
case 4:case 0: state='完结';break;
case 1: state='连载中';break;
case -1: state='已断更';break;
default: state='已下架';break;
}
result[1]=state;
result;

