json=[];
for(i in list){
id=String(list[i]).match(/href/)?String(list[i].attr("href")).match(/(\d+)/)[1]:list[i].novelid;
url="http://www.jjwxc.net/onebook.php?novelid="+id;

html=String(java.ajax(url));
java.setContent(html);
