<js>

function readEncrypt(novelid,flag) {
	cookie=String(source.getLoginHeader())

     	FORUMSIGNTAG = 1;
       READSIGNTAG = 0;
	      strArr = [cookie,novelid,"autosubscribe",flag];

	      sb = new Date().getTime().toString();
         length = strArr.length;
        for ( i2 = READSIGNTAG; i2 < length; i2 += FORUMSIGNTAG) {
            str = strArr[i2];
            sb+=":";
            sb+=str;
        }

	    en= java.createSymmetricCrypto("DES/CBC/PKCS5Padding", String("KW8Dvm2N"), String("1ae2c94b")).encryptBase64(sb)

	      body = "versionCode=313&sign="+en;
option = {
	method:"POST",
	body:String(body)
	}
url = "https://android.jjwxc.com/androidapi/autoSubscribe,"+JSON.stringify(option); java.longToast(JSON.parse(java.ajax(url)).message)
    }

 if(/(开启|关闭)购买/.test(book.getVariable("custom"))){
 	a = /关闭购买/.test(book.getVariable("custom"))?"cancel":(/开启购买/.test(book.getVariable("custom"))?"open":"cancel");
 	readEncrypt(JSON.parse(result)[0].novelid,a)
 	}

result = result=="[]"?JSON.stringify([{"chaptername":"暂无目录","chapterid":"","islock":"0"}]):result;
</js>
