        cache.put(java.md5Encode16(Get('url') + "Account_1"),'');
        cache.put(java.md5Encode16(Get('url') + "accessToken"),'');
        return;
    } else if (Account == '') {
        login('【登录提示】' + '\n\n请输入账号');
        return;
    } else if (Password == '') {
        login('【登录提示】' + '\n\n请输入密码');
        return;
    } else {
            var headers = JSON.parse(cache.get(java.md5Encode16(Get('url') + "headers")));
            var option = {
                "method": "GET",
                "headers": String(headers)
            };
            captcha = JSON.parse(java.ajax(Get('url') + '/prod-api/app-api/vv/auth/get-image,' + JSON.stringify(option))).data;
            Uuid = captcha.uuid;
            Code = java.getVerificationCode('data:image/gif;base64,' + captcha.img);
            var requestBody = '{"username":"' + Account + '","password":"' + Password + '","code":"' + Code + '","uuid":"' + Uuid + '"}';
            var headers = JSON.parse(cache.get(java.md5Encode16(Get('url') + "headers")));
            var option = {
                "method": "POST",
                "body": String(requestBody),
                "headers": String(headers)
            };
            Response = java.ajax(Get('url') + '/prod-api/app-api/vv/auth/login,' + JSON.stringify(option));
            if (JSON.parse(Response).data) {
                cache.put(java.md5Encode16(Get('url') + "Account_1"), Account);
                cache.put(java.md5Encode16(Get('url') + "accessToken"), 'Bearer ' + JSON.parse(Response).data.accessToken);
                login(Put(JSON.stringify('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n账号：' + Account + '\n┋┋' + '\n❤️登录成功❤️\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛')));
            } else {
                cache.put(java.md5Encode16(Get('url') + "Account_1"),'');
                cache.put(java.md5Encode16(Get('url') + "accessToken"),'');
                login('【登录提示】' + '\n\n♣️登录失败♣️\n\n请检查账号和密码');
