		if(type=="text"){
			add=`${name}={{key}}`;
			}else{
				add=`${name}=${value}`
				}
			so.push(add)
		}
		sk=so.join("&")
		//java.log(sk)
		set(a,sk)
		s=a;
		body=sk;
	}else{
		$=JSON.parse(v)
		body=$.body
		s=$.url;
		}
body=body.replace("{{key}}",key);
s;
body;

