ruleToc.chapterList:
@js:
list = [];
J = org.jsoup.Jsoup.parse(result);
$ = (a, b) => Array.from(a.select(b));
u = baseUrl.replace(/more$/, '')
    .replace(source.getKey(), 'https://www.baidu.com/3ubook=');

Name = $(J, '.volumename');
$(J, '.volumelist').forEach((List, i) => {
    A = $(List, 'a');
    if (Name.length > 1 && A.length > 0) {
        list.push({
            text: "📖[" + Name[i].text() + "]📖",
            volume: true
        });
    };
    A.forEach(a => {
        list.push({
            text: a.text(),
            href: u + a.attr("href")
        });
    });
});
