@js:
var bid=java.get('bid');
var url='http://api.vomicmh.com/api/v1/detail/get-comic-detail-chapter-data?mid='+bid;
var head = JSON.parse(java.get('head'));

res=java.get(url,head).body();
var jsonR = JSON.parse(res).data;
var reversedList = jsonR.reverse();
var list = [];
for (var i = 0; i < reversedList.length; i++) {
  var name = reversedList[i].title;
  var link = 'http://www.vomicmh.com/#/page/' + bid + reversedList[i].chapter_url;
  var link = String(link);
  var link = link.replace(/\/m_/, '/');
  list.push({ text: name, href: link });
}
list;

